"use strict";

const { constants } = require("../constants");
const aws = require("aws-sdk");
const SQS = new aws.SQS(constants.sqsConfig());

exports.sqsManager = {
  send: (body, queueUrl) =>
    SQS.sendMessage({
      QueueUrl: queueUrl,
      MessageBody: JSON.stringify(body),
      DelaySeconds: 0,
    }).promise(),
};
